using System.Collections;using System.Collections.Generic;using UnityEngine;public class RollDice : MonoBehaviour {    Rigidbody rb;    bool bottom = false;	// Use this for initialization	void Start () {        rb = this.gameObject.GetComponent<Rigidbody>();	}		// Update is called once per frame	void Update ()    {        if (bottom == false)        {            this.transform.Rotate(new Vector3(Random.Range(0, 10.0f), Random.Range(0, 10.0f), Random.Range(0, 10.0f)));        }        if (Input.GetMouseButtonDown(0))        {            rb.constraints = RigidbodyConstraints.None;        }    }    void OnCollisionEnter(Collision col)    {        bottom = true;    }}